import React, { useRef } from "react";
import "./styles.css";
import sample from "./savefile.json";

import EmailEditor from "react-email-editor";
import axios from "axios";
import { saveAs } from "file-saver";

const App = (props) => {
  const emailEditorRef = useRef(null);
  // console.log("emailEditorRef 1", emailEditorRef);


  const exportHtml = () => {
    emailEditorRef.current.editor.exportHtml((data) => {
      const { design, html } = data;
      const htmlContent = html;
      const blob = new Blob([htmlContent], { type: "text/html;charset=utf-8" });
      saveAs(blob, "hello-world.html");
    });
  };

  const sendEmail = async () => {
    function solve(enteredEmail, html) {
      const parser = new DOMParser();

      // Your string containing the HTML code
      const htmlString = "<h1>Hello, world!</h1><p>This is a paragraph.</p>";

      // Use the parseFromString method to convert the string into a DOM object
      const htmlDocument = parser.parseFromString(html, "text/html");
      var newDiv = document.createElement("div");

      // Step 2: Set properties or attributes of the div element
      newDiv.innerHTML = "This is a new div!";
      newDiv.style.backgroundColor = "#F0F0F0";
      newDiv.style.padding = "20px";
      htmlDocument.body.appendChild(newDiv);
      console.log(htmlDocument);

      const postData = {
        email: enteredEmail,
        html: htmlDocument,
      };
      // console.log(postData);

      axios
        .post("http://localhost:3001/users", postData)
        .then((response) => {
          console.log(response);
        })
        .catch((error) => {
          console.log(error);
        });
    }

    const enteredEmail = prompt("Please enter your test email");
    var pageHTML;

     emailEditorRef.current.editor.exportHtml((data) => {
      pageHTML = data.html;
      solve(enteredEmail, pageHTML);
    });
  };

  const saveDesign = () => {
    emailEditorRef.current.editor.saveDesign((design) => {
      console.log("saveDesign", JSON.stringify(design, null, 4));
      alert("Design JSON has been logged in your developer console.");
    });
  };

  const onDesignLoad = (data) => {
    console.log("onDesignLoad", data);
  };

  const onLoad = () => {
    emailEditorRef.current.editor.addEventListener(
      "onDesignLoad",
      onDesignLoad
    );
    emailEditorRef.current.editor.loadDesign(sample);
  };

  return (
    <div className="container">
      <div className="navbar">
        <button className="save-btn" onClick={saveDesign}>
          Save Design
        </button>
        <button className="save-btn" onClick={exportHtml}>
          Export HTML
        </button>
        <button className="save-btn" onClick={sendEmail}>
          Send Test Email
        </button>
        <button className="save-btn">Send</button>
      </div>

      <EmailEditor ref={emailEditorRef} onLoad={onLoad} minHeight="90vh" />
    </div>
  );
};

export default App;
